#include <sstream>
#include <string>
#include <algorithm>

using namespace std;

const string input = R"(




)";

string day2Solution()
{
    string code = "";

    int x = 1;
    int y = 1;

    istringstream iss(input);
    string line;
    while (getline(iss, line))
    {
        for (char& c : line)
        {
            if (c == 'R') x = min(x + 1, 2);
            if (c == 'L') x = max(x - 1, 0);
            if (c == 'D') y = min(y + 1, 2);
            if (c == 'U') y = max(y - 1, 0);
        }

        int num = y * 3 + x + 1;
        code.append(to_string(num));
    }

    return code;
}